mapper.insert:

	SELECT NVL(max(mno) + 1, 1001) FROM member

INSERT INTO
	MEMBER(mno, name, id, kakaoid, pw, mail, tel, addr, dti_addr)
VALUES(
	#{mno}, #{name}, #{id}, #{kakaoid}, #{pw}, #{mail}, #{tel}, #{addr}, #{dti_addr}
)

mapper.update:
UPDATE imgfile
oriname = #{oriname},
savename = #{savename}
WHERE isshow = 'Y' AND amno = #{amno}
