mapper.select:
SELECT rno, bno, id, bmno, title, body, wdate, larea, marea, sarea, end, endalert, category, click FROM ( SELECT ROWNUM rno, bno, id, bmno, title, body, wdate, larea, marea, sarea, end, endalert, category, click FROM( SELECT bno, bmno, id, title, body, wdate, l.city larea, m.city marea, s.city sarea, end, ((wdate + (end/24)) - sysdate) endalert, category, click FROM board b, largearea l, middlearea m, smallarea s, member WHERE b.larea = l.code AND b.marea = m.code AND b.sarea = s.code AND b.isshow = 'Y' AND bmno = mno
AND LAREA = ( SELECT code FROM largearea WHERE city = #{city} )
AND title LIKE #{search}
AND body LIKE #{search}
AND bmno = (SELECT mno FROM member WHERE id = #{search})
AND marea = (SELECT code FROM middlearea WHERE city LIKE #{search})
ORDER BY b.wdate DESC ) ) WHERE rno BETWEEN #{startCont} AND #{endCont}

SELECT COUNT(*) FROM board WHERE isshow = 'Y'
AND larea = ( SELECT code FROM largearea WHERE city = #{city} )
AND title LIKE #{search}
AND body LIKE #{search}
AND bmno = (SELECT mno FROM member WHERE id = #{search})
AND marea = (SELECT code FROM middlearea WHERE city LIKE #{search})
